(() => {
  return {
    setup({ state, params }) {
      state.particles = [];
      state.maxParticles = 700;
      state.emissionRate = params.emissionRate || 0.6;
    },
    draw({ p, state, helpers }) {
      const width = p.width;
      const height = p.height;
      p.background(8, 10, 18, 245);
      p.noStroke();
      p.fill(20, 28, 45, 200);
      p.rect(width * 0.1, height * 0.15, width * 0.8, height * 0.7, 18);
      const emitCount = Math.floor(3 + (state.emissionRate || 0.6) * 8);
      for (let i = 0; i < emitCount; i += 1) {
        if (state.particles.length >= state.maxParticles) break;
        const xNorm = helpers.random(-1, 1);
        const intensity = helpers.physics.doubleSlitIntensity(xNorm, {
          visibility: state.visibility,
          openSlits: state.openSlits
        });
        if (Math.random() < intensity) {
          const x = width * (0.1 + 0.8 * ((xNorm + 1) / 2));
          const y = height * (0.15 + Math.random() * 0.7);
          state.particles.push({ x, y, life: 1 });
        }
      }
      if (state.particles.length > state.maxParticles) {
        state.particles.splice(0, state.particles.length - state.maxParticles);
      }
      state.particles.forEach((particle) => {
        particle.life *= 0.995;
        p.fill(58, 215, 255, 220 * particle.life + 30);
        p.noStroke();
        p.circle(particle.x, particle.y, 4);
      });
      p.stroke(58, 215, 255, 120);
      p.line(width / 2, height * 0.15, width / 2, height * 0.85);
      p.noStroke();
      p.fill(58, 215, 255, 200);
      const message = state.openSlits ? 'Franges visibles' : 'Une seule fente ouverte';
      p.textAlign(p.CENTER, p.BOTTOM);
      p.text(message, width / 2, height - 12);
    },
    update({ state, key, value }) {
      if (key === 'openSlits') {
        state[key] = Boolean(value);
      } else {
        state[key] = value;
      }
    },
    replay({ state }) {
      state.particles = [];
    }
  };
})()
