(() => {
  return {
    setup({ state, params }) {
      state.atoms = [];
      state.emissionRate = params.emissionRate || 0.8;
    },
    draw({ p, state, helpers }) {
      const width = p.width;
      const height = p.height;
      p.background(8, 10, 18, 245);
      const { separation, spread } = helpers.physics.sternGerlachDistribution(state.angle || 0, state.beamWidth || 0.6);
      p.noStroke();
      p.fill(25, 34, 52, 200);
      p.rect(width * 0.4, height * 0.3, width * 0.2, height * 0.4, 18);
      const newAtoms = Math.floor(2 + (state.emissionRate || 0.8) * 5);
      for (let i = 0; i < newAtoms; i += 1) {
        state.atoms.push({
          x: width * 0.15,
          y: height * 0.5 + (Math.random() - 0.5) * height * 0.15,
          spin: Math.random() > 0.5 ? 1 : -1,
          speed: 2 + Math.random() * 1.5
        });
      }
      for (let i = state.atoms.length - 1; i >= 0; i -= 1) {
        const atom = state.atoms[i];
        atom.x += atom.speed;
        const targetY = height * 0.5 + atom.spin * separation * height * 0.25;
        atom.y += (targetY - atom.y) * 0.03;
        atom.y += (Math.random() - 0.5) * spread;
        p.fill(58, 215, 255, 220);
        p.noStroke();
        p.circle(atom.x, atom.y, 6);
        if (atom.x > width * 0.85) {
          state.atoms.splice(i, 1);
        }
      }
      p.fill(58, 215, 255, 180);
      p.textAlign(p.LEFT, p.TOP);
      p.textSize(14);
      p.text(`Orientation: ${(state.angle || 0).toFixed(0)}Â°`, 20, 20);
      p.text(`SÃ©paration relative: ${(separation * 100).toFixed(0)}%`, 20, 40);
    },
    update({ state, key, value }) {
      state[key] = value;
    },
    replay({ state }) {
      state.atoms = [];
    }
  };
})()
